import { ScrollView,Text, FlatList, StyleSheet } from "react-native";
import React from "react";

const FlatListDemo = () => {
  const names = [
    {
      index: "1",
      name: "table",
    },
    {
      index: "2",
      name: "chair",
    },
    {
      index: "3",
      name: "pen",
    },
    {
      index: "4",
      name: "paper",
    },
    {
        index: "5",
        name: "laptop",
      },
      {
        index: "6",
        name: "Mobile",
      },
      {
        index: "7",
        name: "TV",
      },
  ];

  return (
    <FlatList
      style={styles.txtstyle}
      keyExtractor={(key) => {
        return key.index;
      }}
      horizontal={true}
      data={names}
      renderItem={({ item }) => {
        console.log(item.name);
        return <Text style={styles.txt}>{item.name}</Text>;
      }}
    />
  );
};
const styles = StyleSheet.create({
  txt: {
    padding: 15,
    fontWeight: "bold",
    backgroundColor: "green",
    fontSize: 30,
    color: "rgb(255,255,255)",
    width:20,
    margin: 15,
  },
  txtstyle: {
    margin: 10,
    textAlign: "center",
  }

});

export default FlatListDemo;
