import { FlatList, StyleSheet, Text, View } from "react-native";

export default function Component() {
    const images = [
        {
            index: "1",
            name: "assets/pic2.jpg",
        },
        {
            index: "2",
            name: "assets/pic2.jpg",
        },
        {
            index: "3",
            name: "assets/pic1.jpg"
        },
    ];

    return (
        <View style={styles.img}>
            <FlatList
                style={styles.list}
                keyExtractor={(key) => {
                    return key.index;
                }}
                horizontal={true}
                data={images}
                renderItem={({ item }) => {
                    console.log(item.name);
                    return <Text style={styles.img}>{item.name}</Text>;
                }}
            />
        </View>
    );
};
const styles = StyleSheet.create({
    img: {
        width: 400,
        height: 500,
        textAlign: 'center',
        height: 150,
    }
});


