import { Image,Text, FlatList, StyleSheet } from "react-native";
import React from "react";

const FlatListDemo = () => {
  const names = [
    {
      index: "1",
     name:"assets/pic1.jpg",
    },
    {
      index: "2",
      name:"assets/pic2.jpg",
    },
    {
      index: "3",
      name:"assets/pic3.jpg",
    },
    {
      index: "4",
      name:"assets/pic4.jpg",
    },
    {
      index: "5",
      name: "laptop",
    },
    {
      index: "6",
      name: "Mobile",
    },
    {
      index: "7",
      name: "TV",
    },
  ];

  return (

    <FlatList
      keyExtractor={(key) => {
        return key.index;
      }}
      //numColumns={2}
      Vertical={true}
      data={names}
      renderItem={({ item }) => {
        console.log(item.name);
        return <Text style={styles.img}>{item.name}</Text>;
      }}
    />

  );
};
const styles = StyleSheet.create({
