import { Image, Text, FlatList, StyleSheet } from "react-native";
import React from "react";

const FlatListDemo = () => {
  const names = [
    {
      index: "1",
      name: "table",
    },
    {
      index: "2",
      name: "chair",
    },
    {
      index: "3",
      name: "pen",
    },
    {
      index: "4",
      name: "paper",
    },
    {
      index: "5",
      name: "laptop",
    },
    {
      index: "6",
      name: "Mobile",
    },
    {
      index: "7",
      name: "TV",
    },
  ];

  return (

    <FlatList
      keyExtractor={(key) => {
        return key.index;
      }}
      //numColumns={2}
      Vertical={true}
      inverted
      showsVerticalScrollIndicator={false}
      data={names}
      renderItem={({ item }) => {
        console.log(item.name);
        return <Text style={styles.txt}>{item.name}</Text>;
      }}
    />

  );
};
const styles = StyleSheet.create({
  txt: {
    fontWeight: "bold",
    backgroundColor: "green",
    fontSize: 30,
    color: "rgb(255,255,255)",
    width: 100,
    justifyContent:'center',
    textAlign: "center",
    height: 50,
    borderWidth:5,
    borderColor:'rgb(0,0,0)',
    margin:10
  },

});

export default FlatListDemo;
